import Layout from "./Layout";
import Detail from "./Detail";
import { BrowserRouter as Router, Route } from "react-router-dom";

const Index = (props) => {
  let url = props.url;
  fetch("/api/themes/")
    .then((response) => response.json())
    .then((data) => {
      let themes = data;
      fetch(`/api/${url}`)
        .then((response) => response.json())
        .then((data) => {
          let news = data;
          const themeList = Object.values(themes);
          return (
            <Layout themes={themeList}>
              {news.map((item, number) => {
                let months = [
                  "января",
                  "февраля",
                  "марта",
                  "апреля",
                  "мая",
                  "июня",
                  "июля",
                  "августа",
                  "сентября",
                  "октября",
                  "ноября",
                  "декабря",
                ];

                let today = new Date(item.date);
                let day = today.getDate();
                let month = months[today.getMonth()];
                let year = today.getFullYear();
                let minutes = today.getMinutes();
                let hours = today.getHours();

                let formattedDate = `${hours}:${minutes}`;
                return (
                  <div>
                    <a key={number} href={"/" + item.id}>
                      <h1>{item.title}</h1>
                      <h2>{formattedDate}</h2>
                    </a>
                  </div>
                );
              })}
            </Layout>
          );
        });
    });
};

const App = () => {
  return (
    <Router>
      <div>
        <Route exact path="/" render={() => <Index url="" />} />
        <Route path="/:arg" component={Detail} />
        <Route path="/theme1" render={() => <Index url="politics" />} />
        <Route path="/theme2" render={() => <Index url="region" />} />
        <Route path="/theme3" render={() => <Index url="investments" />} />
      </div>
    </Router>
  );
};

export default App;
