import Layout from "./Layout";
import Detail from "./Detail";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import { useState, useEffect } from "react";
import CroppedImage from "./Img";

const Index = (props) => {
  const [themes, setThemes] = useState([]);
  const [news, setNews] = useState([]);
  useEffect(() => {
    // fetch("/api/themes/")
    fetch("http://localhost:8000/api/themes/")
      .then((response) => response.json())
      .then(async (data) => {
        setThemes(data);
        // const response = await fetch(`/api/${props.url}/`);
        const response = await fetch(`http://localhost:8000/api/${props.url}/`);
        const data_1 = await response.json();
        setNews(data_1);
      });
  }, [props.url]);
  const themeList = Object.values(themes);

  return (
    <Layout themes={themeList}>
      {news.map((item, number) => {
        let img = item.image;
        let months = [
          "января",
          "февраля",
          "марта",
          "апреля",
          "мая",
          "июня",
          "июля",
          "августа",
          "сентября",
          "октября",
          "ноября",
          "декабря",
        ];

        let today = new Date(item.date);
        let day = today.getDate();
        let month = months[today.getMonth()];
        let year = today.getFullYear();
        let minutes = today.getMinutes();
        let hours = today.getHours();
        const intMinutes = parseInt(minutes, 10);
        const formattedMinutes =
          intMinutes < 10 ? `0${intMinutes}` : intMinutes;

        let formattedDate = `${hours}:${formattedMinutes}`;
        return (
          <div>
            <a key={number} href={"/" + item.id}>
              <h1>{item.title}</h1>
              <h2>{formattedDate}</h2>
              <CroppedImage src={img} width={400} height={400} />
            </a>
          </div>
        );
      })}
    </Layout>
  );
};

const App = () => {
  return (
    <BrowserRouter>
      <Routes>
        <Route path="" element={<Index url="" />} />
        <Route path=":arg" element={<Detail />} />
        <Route path="theme1" element={<Index url="politics" />} />
        <Route path="theme2" element={<Index url="region" />} />
        <Route path="theme3" element={<Index url="investments" />} />
      </Routes>
    </BrowserRouter>
  );
};

export default App;
