import React from "react";

const Layout = ({ themes, children }) => {
  return (
    <div>
      <h1>
        <a href="/">Главная</a>
      </h1>
      {themes.map((item, index) => (
        <h2 key={index} className={item}>
          <a href={item.toString().split(",")[0]}>
            {item
              .toString()
              .match(/\d+(.*)$/)[1]
              .replace(",", "")}
          </a>
        </h2>
      ))}
      {children}
      <footer>
        <h1>2023 - KosmoNews</h1>
      </footer>
    </div>
  );
};

export default Layout;
