import Layout from "./Layout";

const App = (news) => {
  news = news["news"];
  return (
    <Layout themes={["Политика", "Инвестиции", "Регионы"]}>
      {news.map((item, number) => {
        let months = [
          "января",
          "февраля",
          "марта",
          "апреля",
          "мая",
          "июня",
          "июля",
          "августа",
          "сентября",
          "октября",
          "ноября",
          "декабря",
        ];

        let today = new Date(item.date);
        let day = today.getDate();
        let month = months[today.getMonth()];
        let year = today.getFullYear();

        let formattedDate = `${day} ${month} ${year}`;
        return (
          <div>
            <a key={number} href={"/" + item.id}>
              <h1>{item.title}</h1>
              <h2>{formattedDate}</h2>
            </a>
          </div>
        );
      })}
    </Layout>
  );
};

export default App;
