import Layout from "./Layout";
import Detail from "./Detail";
import { BrowserRouter, Routes, Route } from "react-router-dom";

const Index = (props) => {
  let url = props.url;
  fetch("http://127.0.0.1:8000/api/themes/")
    .then((response) => response.json())
    .then((data) => {
      let themes = data;
      fetch(`http://127.0.0.1:8000/api/${url}`)
        .then((response) => response.json())
        .then((data) => {
          let news = data;
          const themeList = Object.values(themes);
          return (
            <Layout themes={themeList}>
              {news.map((item, number) => {
                let months = [
                  "января",
                  "февраля",
                  "марта",
                  "апреля",
                  "мая",
                  "июня",
                  "июля",
                  "августа",
                  "сентября",
                  "октября",
                  "ноября",
                  "декабря",
                ];

                let today = new Date(item.date);
                let day = today.getDate();
                let month = months[today.getMonth()];
                let year = today.getFullYear();
                let minutes = today.getMinutes();
                let hours = today.getHours();

                let formattedDate = `${hours}:${minutes}`;
                return (
                  <div>
                    <a key={number} href={"/" + item.id}>
                      <h1>{item.title}</h1>
                      <h2>{formattedDate}</h2>
                    </a>
                  </div>
                );
              })}
            </Layout>
          );
        });
    });
};

const Manifest = () => {
  return {
    short_name: "React App",
    name: "Create React App Sample",
    icons: [
      {
        src: "favicon.ico",
        sizes: "64x64 32x32 24x24 16x16",
        type: "image/x-icon",
      },
      {
        src: "logo192.png",
        type: "image/png",
        sizes: "192x192",
      },
      {
        src: "logo512.png",
        type: "image/png",
        sizes: "512x512",
      },
    ],
    start_url: ".",
    display: "standalone",
    theme_color: "#000000",
    background_color: "#ffffff",
  };
};

const App = () => {
  return (
    <BrowserRouter>
      <Routes>
        <Route path="" render={() => <Index url="" />} />
        <Route path=":arg" component={Detail} />
        <Route
          path="//%PUBLIC_URL%/manifest.json"
          component={() => <Manifest />}
        />
        <Route path="theme1" render={() => <Index url="politics" />} />
        <Route path="theme2" render={() => <Index url="region" />} />
        <Route path="theme3" render={() => <Index url="investments" />} />
      </Routes>
    </BrowserRouter>
  );
};

export default App;
