import Layout from "./Layout";

const Detail = ({ match }) => {
  const { arg } = match.params;
  let date = new Date(news["date"]);
  let title = news["title"];
  let article_text = news["article_text"];
  let theme = themes[news["theme"]];
  const themeList = Object.values(themes);

  let months = [
    "января",
    "февраля",
    "марта",
    "апреля",
    "мая",
    "июня",
    "июля",
    "августа",
    "сентября",
    "октября",
    "ноября",
    "декабря",
  ];

  let today = new Date(date);
  let day = today.getDate();
  let month = months[today.getMonth()];
  let year = today.getFullYear();
  let minutes = today.getMinutes();
  let hours = today.getHours();

  let formattedDate = `${hours}:${minutes}. ${day} ${month}`;

  return (
    <Layout themes={themeList}>
      <div>
        <h1>{title}</h1>
        <h3>{theme}</h3>
        <h4>{formattedDate}</h4>
        <h2>{article_text}</h2>
      </div>
    </Layout>
  );
};

export default Detail;
