import Layout from "./Layout";
import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

const Detail = () => {
  const { arg } = useParams();
  console.log(arg);
  const [news, setNews] = useState({});
  const [themes, setThemes] = useState([]);

  useEffect(() => {
    fetch("http://127.0.0.1:8000/api/themes/")
      .then((response) => response.json())
      .then(async (data) => {
        setThemes(data);
        const response = await fetch(`http://127.0.0.1:8000/api/${arg}`);
        const data_1 = await response.json();
        setNews(data_1);
      });
  }, [arg]);

  // Форматирование даты
  const formatDate = (date) => {
    let months = [
      "января",
      "февраля",
      "марта",
      "апреля",
      "мая",
      "июня",
      "июля",
      "августа",
      "сентября",
      "октября",
      "ноября",
      "декабря",
    ];

    let today = new Date(date);
    let day = today.getDate();
    let month = months[today.getMonth()];
    let year = today.getFullYear();
    let minutes = today.getMinutes();
    let hours = today.getHours();

    return `${hours}:${minutes}. ${day} ${month}`;
  };

  const themeList = Object.values(themes);
  const formattedDate = formatDate(news.date); // Здесь news.date предполагается как строка даты

  return (
    <Layout themes={themeList}>
      <div>
        <h1>{news.title}</h1>
        <h3>{themes[news.theme]}</h3>
        <h4>{formattedDate}</h4>
        <h2>{news.article_text}</h2>
      </div>
    </Layout>
  );
};

export default Detail;
